import React from 'react';

class Inpsquare extends React.Component {
    constructor(props) {
        super(props);
        this.state = { name: "", description: "", geo: ""};
        this.render = this.render.bind(this);
        this.handleChangeSurname = this.handleChangeSurname.bind(this);
        this.handleChangeName = this.handleChangeName.bind(this);
        this.handleChangePatronymic = this.handleChangePatronymic.bind(this);
        this.handleChangeEmail = this.handleChangeEmail.bind(this);
        this.handleChangePassword = this.handleChangePassword.bind(this);
        this.reg = this.reg.bind(this)
    }

    handleChangeDescription(e){
        this.setState({description: e.target.value});
    }

    handleChangeName(e){
        this.setState({name: e.target.value});
    }

    handleChangeGeo(e){
        this.setState({geo: e.target.value});
    }

    reg() {
        var url = 'https://cors-anywhere.herokuapp.com/' + 'http://95.213.38.13:8000/addsq';
        let body = {
            'description': this.state.description,'name':this.state.name ,'geo':this.state.geo
        };
        fetch(url, {
            method: 'POST',
            headers:{
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(body)
        }).then(res => res.json())
            .then(response => console.log('Успех:', JSON.stringify(response)))
            .catch(error => console.error('Ошибка:', error));
    }

    render() {
        return (
            <div className="regWindow">
                <div className="window">
                    <div>
                        <div><center>
                            <h2><font color="white">Добавление площадки</font></h2></center>
                        </div>

                        <div className="pad">
                            <input type="text" className="form-control" placeholder="Фамилия" value={this.state.surname}
                                   onChange={this.handleChangeSurname}></input>

                            <input type="text" className="form-control" placeholder="Имя" value={this.state.name}
                                   onChange={this.handleChangeName}></input>

                            <input type="text" className="form-control" placeholder="Отчество" value={this.state.patronymic}
                                   onChange={this.handleChangePatronymic}></input>
                        </div>
                        <button type="button" className="btn btn-outline-warning pad" onClick={this.reg()}>Добавить</button>
                    </div>
                </div>
            </div>
        );
    }
}
export default Inpsquare;
